import React from "react";
import { StyleSheet, View, Image } from "react-native";

import { AppText as Text } from "../index";

import location from "../../../assets/location.png";
import arrow from "../../../assets/arrow.png";

export default ({ title, address, distance, quantity, company, price }) => (
  <View style={styles.view}>
    <Text style={styles.title}>{title}</Text>
    <View style={styles.rowList}>
      <Image source={location}/>
      <Text style={styles.text1}>{address[0]}</Text>
      <Text style={styles.text2}></Text>
      <Text style={styles.text1}>{distance[0]}</Text>
    </View>
    <View style={styles.rowList}>
      <Image source={arrow}/>
      <Text style={styles.text3}>{quantity}</Text>
    </View>
    <View style={styles.rowList}>
      <Image source={location}/>
      <Text style={styles.text1}>{address[1]}</Text>
      <Text style={styles.text2}></Text>
      <Text style={styles.text1}>{distance[1]}</Text>
    </View>
    <View style={styles.bottom}>
      <Text style={styles.text3}>{company}</Text>
      <Text style={styles.text4}>{price}</Text>
    </View>
  </View>
);

const styles = StyleSheet.create({
  view: {
    padding: "18px",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "flex-start",
    border: "1px solid rgba(175, 177, 184, 0.8)",
    boxSizing: "border-box",
    borderRadius: "10px"
  },
  title: {
    fontWeight: "bold",
    color: "#333333",
    marginBottom: "16px"
  },
  rowList: {
    display: "flex",
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "flex-start",
    marginBottom: "6px"
  },
  text1: {
    color: "#333333"
  },
  text2: {
    width: "3px",
    height: "3px",
    background: "#BDBDBD"
  },
  text3: {
    fontSize: "14px",
    lineHeight: "16px",
    color: "#828282"
  },
  text4: {
    fontWeight: "bold",
    fontSize: "18px",
    lineHeight: "21px",
    color: "#333333"
  },
  bottom: {
    width: "100%",
    display: "flex",
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between"
  }
});
